import axios from 'axios';

import './App.css';

function App() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [audioSource, setAudioSource] = useState('');

  useEffect(() => {
    fetchRecordings();
  }, []);

  const fetchRecordings = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/recordings');
      setRecordings(response.data);
    } catch (error) {
      console.error('Failed to fetch the recordings:', error);
    }
  };

  const startRecording = () => {
    setIsRecording(true);
  };

  const stopRecording = () => {
    setIsRecording(false);
  };

  const handleAudioSourceChange = (event) => {
    setAudioSource(URL.createObjectURL(event.target.files[0]));
  };

  const onSave = async () => {
    try {
      const formData = new FormData();
      formData.append('recording', audioSource);
      const response = await axios.post('http://localhost:5000/api/recordings', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setRecordings(prevRecordings => [...prevRecordings, response.data]);
      setAudioSource('');
    } catch (error) {
      console.error('Failed to save the recording:', error);
    }
  };

  return (
    <div className="app-container">
      <h1>Voice Recording Demo</h1>

      <h2>Recordings:</h2>
      {recordings.length > 0 ? (
        <ul>
          {recordings.map(recording => (
            <li key={recording._id}>
              <audio controls src={`http://localhost:5000/${recording.path}`} />
              <p>{recording.filename}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No recordings available.</p>
      )}

      <h2>Record:</h2>
      {!isRecording ? (
        <>
          <input type="file" accept="audio/*" onChange={handleAudioSourceChange} />
          <button onClick={startRecording} disabled={!audioSource}>Start Recording</button>
        </>
      ) : (
        <>
          <button onClick={stopRecording}>Stop Recording</button>
          <button onClick={onSave} disabled={!audioSource}>Save Recording</button>
        </>
      )}
    </div>
  );
}

export default App;
