import React, { useState, useRef } from 'react';
import RecordRTC from 'recordrtc';
import './App.css';

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [recordings, setRecordings] = useState([]);

  const mediaRecorderRef = useRef(null);
  const streamRef = useRef(null);

  const startRecording = async () => {
    try {
      streamRef.current = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new RecordRTC(streamRef.current, {
        type: 'audio',
        mimeType: 'audio/webm'
      });
      mediaRecorderRef.current.startRecording();
      setIsRecording(true);
    } catch (error) {
      console.error('Failed to start recording:', error);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stopRecording(() => {
        const blob = mediaRecorderRef.current.getBlob();
        const blobURL = URL.createObjectURL(blob);
        setRecordings(prevRecordings => [...prevRecordings, blobURL]);
      });
      setIsRecording(false);
    }
  };

  const saveRecording = async (recordingURL) => {
    console.log('Saving recording:', recordingURL);
    try {
      const response = await fetch('/api/upload', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ recordingURL }),
      });

      if (response.ok) {
        console.log('Recording saved successfully');
      } else {
        console.error('Failed to save recording');
      }
    } catch (error) {
      console.error('Failed to save recording:', error);
    }




  };

  return (
    <div className="app-container">
      <h1 className="app-title">Voice Recording Demo</h1>

      <div className="recordings-container">
        <h2 className="recordings-title">Unsaved Recordings:</h2>
        <ul className="recordings-list">
          {console.log("reco", recordings.length)}
          {recordings.map((recording, index) => (
            <li key={index} className="recording-item">
              <audio controls src={recording} className="recording-audio" />
              <button
                className="save-button"
                onClick={() => saveRecording(recording)}
              >
                Save
              </button>
            </li>
          ))}
        </ul>
      </div>
      <div className="record-button-container">
        <h2 className="record-title">Record:</h2>
        {isRecording ? (
          <button className="stop-button" onClick={stopRecording}>
            Stop Recording
          </button>
        ) : (
          <button className="start-button" onClick={startRecording}>
            Start Recording
          </button>
        )}
      </div>
      <div>

      </div>

      <button className="fetch-button" onClick={startRecording}>
        Fetch Recordings
      </button>
      <ul className="recordings-list">
        {console.log("reco", recordings.length)}
        {recordings.map((recording, index) => (
          <li key={index} className="recording-item">
            <audio controls src={recording} className="recording-audio" />
            <button
              className="save-button"
              onClick={() => saveRecording(recording)}
            >
              Save
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
