import React, { useState, useRef } from 'react';
import axios from 'axios';

function App() {
  const [recordings, setRecordings] = useState([]);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      mediaRecorderRef.current.addEventListener('dataavailable', handleDataAvailable);
      mediaRecorderRef.current.start();
    } catch (error) {
      console.error('Failed to start recording:', error);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
    }
  };

  const handleDataAvailable = (event) => {
    if (event.data.size > 0) {
      chunksRef.current.push(event.data);
    }
  };

  const onSave = async () => {
    try {
      const blob = new Blob(chunksRef.current, { type: 'audio/webm' });
      const formData = new FormData();
      formData.append('recording', blob);

      const response = await axios.post('http://localhost:3000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setRecordings(prevRecordings => [response.data, ...prevRecordings]);
      chunksRef.current = [];
    } catch (error) {
      console.error('Failed to save the recording:', error);
    }
  };

  const fetchRecordings = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/recordings');
      if (response.ok) {
        const data = await response.json();
        setRecordings(data);
      } else {
        throw new Error('Failed to fetch the recordings');
      }
    } catch (error) {
      console.error('Failed to fetch the recordings:', error);
    }
  };


  return (
    <div>
      <h1>Voice Recording Demo</h1>

      <button onClick={fetchRecordings}>Fetch Recordings</button>

      <h2>Recordings:</h2>
      <ul>
        {recordings.map(recording => (
          <li key={recording._id}>
            <audio controls src={`http://localhost:3000/${recording.path}`} />
            <p>{recording.filename}</p>
          </li>
        ))}
      </ul>

      <h2>Record:</h2>
      <button onClick={startRecording} type="button">Start</button>
      <button onClick={stopRecording} type="button">Stop</button>
      <button onClick={onSave} type="button">Save</button>
    </div>
  );
}

export default App;
