import React, { useState } from 'react';
import axios from 'axios';
import { ReactMic } from 'react-mic';

function App() {
  const [recordings, setRecordings] = useState([]);

  const startRecording = () => {
    console.log('Recording started');
  };

  const stopRecording = () => {
    console.log('Recording stopped');
  };

  const onData = () => {
    console.log('Data available');
  };

  const onSave = async (blobObject) => {
    try {
      const formData = new FormData();
      formData.append('recording', blobObject.blob);

      const response = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setRecordings(prevRecordings => [response.data, ...prevRecordings]);
    } catch (error) {
      console.error('Failed to save the recording:', error);
    }
  };

  const fetchRecordings = async () => {
    try {
      const response = await axios.get('/api/recordings');
      setRecordings(response.data);
    } catch (error) {
      console.error('Failed to fetch the recordings:', error);
    }
  };

  return (
    <div>
      <h1>Voice Recording Demo</h1>

      <button onClick={fetchRecordings}>Fetch Recordings</button>

      <h2>Recordings:</h2>
      <ul>
        {recordings.map(recording => (
          <li key={recording._id}>
            <audio controls src={recording.path} />
            <p>{recording.filename}</p>
          </li>
        ))}
      </ul>

      <h2>Record:</h2>
      <ReactMic
        record={false}
        className="sound-wave"
        onStop={onSave}
        onData={onData}
        strokeColor="#000000"
        backgroundColor="#FF4081"
      />
      <button onClick={startRecording} type="button">Start</button>
      <button onClick={stopRecording} type="button">Stop</button>
    </div>
  );
}

export default App;
